import React, { useState } from "react";

// Function to handle JSON file drop
const handleDrop = (event, setParsedData, setIsJsonUploaded) => {
  event.preventDefault();
  const file = event.dataTransfer.files[0];
  if (file && file.type === "application/json") {
    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const json = JSON.parse(e.target.result);

        // Parsing the specific data you need
        const connections = json.connections;
        const data = Object.keys(connections).map((key) => {
          const connection = connections[key];
          const connectionType =
            connection.attributes.connection_type.button_added;
          const node1 = connection.node_id_1;
          const node2 = connection.node_id_2;
          const section = connection.sections.midpoint_section;
          const latitude = section.latitude;
          const longitude = section.longitude;

          return {
            connectionType,
            node1,
            node2,
            latitude,
            longitude,
          };
        });

        setParsedData(data);
        setIsJsonUploaded(true);
      } catch (error) {
        console.error("Error parsing JSON", error);
      }
    };
    reader.readAsText(file);
  }
};

function App() {
  const [parsedData, setParsedData] = useState([]);
  const [isJsonUploaded, setIsJsonUploaded] = useState(false);

  // Group data by connection type
  const groupedData = parsedData.reduce((acc, item) => {
    acc[item.connectionType] = acc[item.connectionType] || [];
    acc[item.connectionType].push(item);
    return acc;
  }, {});

  return (
    <div className="min-h-screen flex flex-col">
      {/* Header */}
      <header className="bg-blue-600 text-white py-4 px-8 shadow-md">
        <h1 className="text-xl font-semibold">MMU JSON Parser</h1>
      </header>

      <main className="flex-grow p-8 bg-gray-100">
        {isJsonUploaded ? (
          <div className="text-gray-800">
            <h3 className="text-lg font-semibold mb-4">Parsed Data:</h3>
            <div className="bg-white border border-gray-300 rounded p-4">
              {Object.keys(groupedData).map((type, index) => (
                <div key={index} className="mb-6">
                  <h4 className="text-md font-semibold mb-2">{type}</h4>
                  <div className="grid grid-cols-2 gap-4">
                    {groupedData[type].map((item, idx) => (
                      <div key={idx} className="p-2 border-b border-gray-200">
                        <p>
                          <strong>Node 1:</strong> {item.node1}
                        </p>
                        <p>
                          <strong>Node 2:</strong> {item.node2}
                        </p>
                        <p>
                          <strong>Location:</strong> ({item.latitude},{" "}
                          {item.longitude})
                        </p>
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        ) : (
          <div
            onDragOver={(e) => e.preventDefault()}
            onDrop={(e) => handleDrop(e, setParsedData, setIsJsonUploaded)}
            className="w-full h-48 border-4 border-dashed border-gray-300 flex items-center justify-center bg-white text-center cursor-pointer hover:border-gray-500 transition duration-300"
          >
            Drop your JSON file here
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
